sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	// Set the default colors for background and font
	var theBGColor = '#999999';
	var theColor   = '#FFFFFF';

	// Get the classification information from the form
	var theClass = g_form.getValue( 'u_classification' ) || '';
	var theClassBy = ('Classified By: ' + g_form.getValue( 'u_classified_by' )) || '';
	var theDerived = ('Derived From: ' + g_form.getValue( 'u_derived_from' )) || '';
	var theDeclass = ('Declassification Date: ' + g_form.getValue( 'u_declassification_date' )) || '';

	// Update the header / footer banners with the classification data
	var theDoc = document || top.window.document.activeElement.contentDocument;
	theDoc.getElementById( 'class_header' ).innerHTML = theClass;
	theDoc.getElementById( 'class_footer' ).innerHTML = theClass;
	theDoc.getElementById( 'class_by' ).innerHTML = theClassBy;
	theDoc.getElementById( 'derived_from' ).innerHTML = theDerived;
	theDoc.getElementById( 'declass_on' ).innerHTML = theDeclass;

	// Set correct header / footer banners colors for background and font, hide footer block if appropriate
	theDoc.getElementById( 'class_footer_block' ).style.display = 'block';
	if (theClass.startsWith( 'UNCLASSIFIED' ) == true) {
		theBGColor = '#008000';
		theDoc.getElementById( 'class_footer_block' ).style.display = 'none';
	} else if (theClass.startsWith( 'CONFIDENTIAL' ) == true) {
		theBGColor = '#0000FF';
	} else if (theClass.startsWith( 'SECRET' ) == true) {
		theBGColor = '#FF0000';
	} else if (theClass.startsWith( 'TOP SECRET' ) == true) {
		theBGColor = '#FFFF20';
		theColor   = '#000000';
	}

	// Apply background and font colors to header / footer banners
	theDoc.getElementById( 'class_header_row' ).style.backgroundColor = theBGColor;
	theDoc.getElementById( 'class_header_row' ).style.color = theColor;
	theDoc.getElementById( 'class_footer_row' ).style.backgroundColor = theBGColor;
	theDoc.getElementById( 'class_footer_row' ).style.color = theColor;
}
