function onLoad() {
   //Type appropriate comment here, and begin script below
	g_form.setDisplay("number", false);
   var x = Math.floor((Math.random() * 20000) + 1);
	var cNum = g_form.getValue("number");
	var gd = new Date();     //GlideDate();
	var cYear = gd.getFullYear().toString();
	var cDate = gd.getDate().toString();
	var cMonth = gd.getMonth() + 1;

	if(g_form.isNewRecord()){
		g_form.setValue("conf_num",cYear + "-" + cDate + "-" + cMonth.toString() + "- CON" + x); //g_form.getValue("number"));

	}

}
