function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
if(newValue == "LX Compound"){
	g_form.clearValue("building_address");
	g_form.clearValue("city");
	g_form.clearValue("m_states");
	g_form.clearValue("zip_code");
	g_form.clearValue("u_country");
} else if (newValue == "IC Facility" || newValue == "Government Facility" || newValue == "Commercial Facility"){
	g_form.clearValue("conf_room_location");
}
   //Type appropriate comment here, and begin script below

}
